<span class="line"><span style="--s-dark:#B8A965;--s-light:#998418;">    plugins</span><span style="--s-dark:#666666;--s-light:#999999;">:</span><span style="--s-dark:#666666;--s-light:#999999;"> [</span><span style="--s-dark:#80A665;--s-light:#59873A;">vue</span><span style="--s-dark:#666666;--s-light:#999999;">()],</span></span>
